mapper.select:
SELECT 

FROM feedback a

WHERE a.id = #{id}

SELECT 

FROM feedback a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM feedback a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE feedback SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
