mapper.update:
UPDATE lesson_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT DISTINCT

FROM lesson_info a

	a.state = 1 AND a.del_flag = 0
	AND a.business_info_id = #{businessInfoId}

ORDER BY a.recommend desc, a.create_date desc
LIMIT #{showCount}

SELECT DISTINCT

FROM lesson_info a

	a.state = 1 AND a.del_flag = 0
	AND a.business_info_id = #{businessInfoId}

ORDER BY a.recommend desc, a.create_date desc
LIMIT #{pageIndex},#{numPerPage}
