mapper.update:
UPDATE my_collect SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE my_collect SET 
	del_flag = 1
WHERE account_id=#{0} and village_info_id=#{1} and business_info_id=#{2}

mapper.select:
SELECT ,b.business_name AS "businessName",b.business_pic AS "businessPic" ,b.business_label AS "businessLabel",b.distribute_charge AS "distributeCharge",b.free_distribute_flag AS "freeDistributeFlag" FROM my_collect a,business_info b
a.del_flag = 0 AND b.del_flag = 0 AND a.business_info_id = b.id AND a.account_id = #{accountId} AND a.village_info_id = #{villageInfoId}
ORDER BY a.update_date DESC
