mapper.select:
SELECT 

FROM order_field a

WHERE a.id = #{id}

SELECT
FROM order_field a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM order_field a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE order_field SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT DISTINCT , b.ID AS Business_Info_Id, b.Business_Name, b.Business_Pic, c.Appointment_Time FROM Order_Field a, Business_Info b, Order_Field_List c WHERE a.Business_Info_ID = b.ID AND a.ID = c.Order_Field_ID AND a.Del_Flag = 0 AND b.del_flag = 0 AND c.Del_Flag = 0 AND a.Village_Info_ID = #{villageInfoId} AND a.Account_ID = #{accountId}
AND a.Module_Manage_ID = #{moduleManageId}
ORDER BY a.Create_Date DESC LIMIT 1
