mapper.select:
SELECT 

FROM order_lesson a

WHERE a.id = #{id}

SELECT
FROM order_lesson a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM order_lesson a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE order_lesson SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
