mapper.select:
SELECT 

FROM service_info a

WHERE a.id = #{id}

SELECT
FROM service_info a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM service_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE service_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT DISTINCT

FROM service_info a

	a.state = 1 AND a.del_flag = 0
	AND a.business_info_id = #{businessInfoId}

ORDER BY a.recommend desc, a.create_date desc
LIMIT #{showCount}

SELECT DISTINCT
FROM service_info a WHERE a.state = '1' AND a.del_flag = '0' AND a.business_info_id = #{businessInfoId}
AND a.Sort_Info_ID = #{sortInfoId}
ORDER BY a.recommend DESC, a.create_date DESC
