mapper.select:
SELECT 

FROM verify_code_record a

WHERE a.id = #{id}

SELECT 

FROM verify_code_record a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM verify_code_record a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE verify_code_record SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT COUNT(a.ID) FROM verify_code_record a 
WHERE a.del_flag=0 AND DATEDIFF(NOW(),a.Send_Time) = 0 
AND a.phone_num=#{phoneNum} AND a.code_type=#{codeType}
