mapper.select:
SELECT 

FROM village_linerecomspecialdetail a

WHERE a.id = #{id}

SELECT 

FROM village_linerecomspecialdetail a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM village_linerecomspecialdetail a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE village_linerecomspecialdetail SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
