mapper.select:
SELECT 

FROM social_speak a

WHERE a.id = #{id}

SELECT , vi.village_name AS "villageinfoname", ss.subname AS "subjectname", (select count(*) from social_forward sf where sf.speakId = a.id) as sumforward, (select count(*) from social_comment sc where sc.speakId = a.id) as sumcomment, (select su.name from sys_user su where su.id = a.userid) as username FROM village_info vi RIGHT JOIN social_speak a on vi.id = a.villageinfoid LEFT JOIN social_sub_relation ssr on a.id=ssr.speakId LEFT JOIN social_subject ss ON ssr.subjectId = ss.id
AND a.content like '%'||#{content}||'%' '%'+#{content}+'%' concat('%',#{content},'%')
AND a.createtime BETWEEN #{beginCreatetime} AND #{endCreatetime}
AND a.villageinfoid IN( SELECT c.id FROM village_info c WHERE c.addr_pro = '${addrpro}' )
AND a.villageinfoid IN( SELECT d.id FROM village_info d WHERE d.addr_city = '${addrcity}' )
AND a.villageinfoid = ${villageInfoId}
AND a.delflag = ${delflag}
ORDER BY ${page.orderBy}
ORDER BY a.istop desc, a.createtime desc

SELECT 

FROM social_speak a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE social_speak SET
	delflag = #{delflag}
WHERE id = #{id}

UPDATE social_speak SET
	istop = #{istop}
WHERE id = #{id}

mapper.select:
SELECT , (SELECT COUNT(*) FROM social_relation r WHERE r.userId = #{userId} AND r.subUserId = a.userId) AS isFocus, (SELECT COUNT(*) FROM social_praise p WHERE p.userId = #{userId} AND p.pid = a.id AND p.state = 1) AS isPraise, (SELECT COUNT(*) FROM social_forward f where f.speakid = a.id ) as countForward, (SELECT COUNT(*) FROM social_comment c where c.speakid = a.id ) as countComment, (select count(*) from social_praise pr where pr.pid = a.id AND pr.state = 1) as countPraise, ac.Nickname as nickName, ac.photo as photo FROM social_speak a left join account ac on a.userid = ac.id where 1 = 1
AND a.villageInfoId = #{socialSpeak.villageinfoid}
AND a.delflag = #{socialSpeak.delflag}
AND a.userId = (SELECT subUserId FROM social_relation r WHERE r.userId = #{userId})
order by a.createTime DESC limit #{pageIndex},#{pageSize}
