mapper.select:
SELECT 

FROM business_balance_detail a

WHERE a.id = #{id}

		SELECT 

		FROM business_balance_detail a


			a.del_flag = #{DEL_FLAG_NORMAL}

                AND a.business_balance_Id = #{businessBalanceId}


				AND a.pay_type = #{payType}


                AND a.prod_type = #{prodType}


				AND a.trade_complete_time between #{balanceStartTime} and date_add(#{balanceEndTime}, interval 1 DAY)




				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC

		SELECT 

		FROM business_balance_detail a


			a.del_flag = #{DEL_FLAG_NORMAL}

                AND a.business_balance_Id = #{businessBalanceId}


                AND a.pay_type = #{payType}


                AND a.prod_type = #{prodType}


                AND a.trade_complete_time between #{balanceStartTime} and date_add(#{balanceEndTime}, interval 1 DAY)




				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC

mapper.update:
UPDATE business_balance_detail SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT @rownum := @rownum + 1 AS serialNum, a.trade_complete_time AS tradeCompleteTime, a.order_group_no AS orderNo, a.prod_type as prodType, a.pay_type AS payType, a.pay_org AS payOrg, a.order_money AS orderMoney, a.deduction_money AS deductionMoney FROM (SELECT @rownum := 0) r, business_balance_detail a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.business_balance_Id = #{businessBalanceId}
AND a.pay_type = #{payType}
AND a.prod_type = #{prodType}
AND a.trade_complete_time between #{balanceStartTime} and date_add(#{balanceEndTime}, interval 1 DAY)
