mapper.select:
SELECT 

FROM field_info a

WHERE a.id = #{id}

select a.*,(a.end_time>now()) AS "outTimeState" from field_partition_price a 
where a.Field_Info_ID=#{id} and a.Del_Flag=0 and  TO_DAYS(a.appointment_time)=#{appointmentTime}
order by Start_Time

mapper.update:
UPDATE field_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
