mapper.select:
SELECT 

FROM field_info_price a

WHERE a.id = #{id}

SELECT 

FROM field_info_price a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.business_info_id = #{businessInfoId}


		AND a.field_info_id = #{fieldInfoId}




		ORDER BY ${page.orderBy}


		ORDER BY a.start_open_time ASC

SELECT 

FROM field_info_price a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE field_info_price SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE field_info_price SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE field_info_id = #{fieldInfoId}
