mapper.select:
SELECT 

FROM field_partition_price a

WHERE a.id = #{id}

SELECT 

FROM field_partition_price a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.business_info_id = #{businessInfoId}


		AND a.field_info_id = #{fieldInfoId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM field_partition_price a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.business_info_id = #{businessInfoId}


		AND a.field_info_id = #{fieldInfoId}


		AND TO_DAYS(a.appointment_time) = TO_DAYS(#{appointmentTime})




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM field_partition_price a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE field_partition_price SET
	state = #{state}
WHERE id = #{id}

UPDATE field_partition_price SET
	state = '0'
WHERE id = #{id}

UPDATE field_partition_price SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
select * from field_partition_price where DATE_FORMAT(start_time ,'%Y%m%d')= #{ymd}
AND business_info_id = '${@com.its.modules.sys.utils.UserUtils@getUser().businessinfoId}'
 order by field_info_id
