mapper.select:
SELECT 

FROM sort_info a

WHERE a.id = #{id}

SELECT 

FROM sort_info a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.business_info_id = #{businessInfoId}


        AND a.type = #{type}




        ORDER BY ${page.orderBy}


        ORDER BY a.sort_order ASC, a.update_date DESC

SELECT 

FROM sort_info a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.business_info_id = #{businessInfoId}


        AND a.type = #{type}




        ORDER BY ${page.orderBy}


        ORDER BY a.sort_order ASC, a.create_date DESC

mapper.update:
UPDATE sort_info SET     
    business_info_id = #{businessInfoId},
    name = #{name},
    sort_order = #{sortOrder}
WHERE id = #{id}

UPDATE sort_info SET 
    del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
