mapper.select:
SELECT 

FROM order_lesson a

WHERE a.id = #{id}

		SELECT 

		FROM order_lesson a


			a.del_flag = #{DEL_FLAG_NORMAL}

                AND a.order_no = #{orderNo}


                AND a.order_state = #{orderState}


                AND a.pay_state = #{payState}


                AND a.create_date >=  #{beginCreateDate}


                AND a.create_date <  DATE_ADD(#{endCreateDate},INTERVAL 1 DAY)


                AND a.business_info_id = #{businessInfoId}


                AND a.prod_type = #{prodType}


                AND a.order_state != 1 AND a.order_state != 2


                AND a.order_state != 2




				ORDER BY ${page.orderBy}


				ORDER BY a.create_date DESC

SELECT 

FROM order_lesson a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE order_lesson SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE order_lesson SET 
    order_state = "2",
    update_by = #{updateBy.id},
    update_date = #{updateDate}
WHERE order_no = #{orderNo}
