SELECT 

FROM business_unit a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.business_info_id = #{businessInfoId}


        AND a.name LIKE 
            '%'||#{name}||'%'
            '%'+#{name}+'%'
            concat('%',#{name},'%')




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

