mapper.select:
SELECT

FROM adver_position a

WHERE a.id = #{id}

SELECT

FROM adver_position a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.module_code = #{moduleCode}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

SELECT

FROM adver_position a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.module_code = #{moduleCode}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT
COUNT(1)
FROM adver_position a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.module_code = #{moduleCode}
	AND a.open_screen_flag = ${@com.its.common.config.Global@YES}

SELECT 

FROM adver_position a
WHERE a.position_name = #{positionName}
AND a.module_code = #{moduleCode}
AND a.del_flag = '0'

mapper.update:
UPDATE adver_position SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
