mapper.select:
SELECT 

FROM business_balance_detail a

WHERE a.id = #{id}

		SELECT 

		FROM business_balance_detail a


			a.del_flag = #{DEL_FLAG_NORMAL}

                AND a.business_balance_Id = #{businessBalanceId}


				AND a.pay_type = #{payType}


				AND a.trade_complete_time between #{balanceStartTime} and date_add(#{balanceEndTime}, interval 1 DAY)




				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC

		SELECT 

		FROM business_balance_detail a


			a.del_flag = #{DEL_FLAG_NORMAL}

                AND a.business_balance_Id = #{businessBalanceId}


                AND a.pay_type = #{payType}


                AND a.trade_complete_time between #{balanceStartTime} and date_add(#{balanceEndTime}, interval 1 DAY)




				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC

mapper.update:
UPDATE business_balance_detail SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE order_goods SET
check_time = now(),
check_state = '1' WHERE order_no = #{orderNo}

UPDATE order_service SET
check_time = now(),
check_state = '1' WHERE order_no = #{orderNo}

UPDATE order_lesson SET
check_time = now(),
check_state = '1' WHERE order_no = #{orderNo}

UPDATE order_field SET
check_time = now(),
check_state = '1' WHERE order_no = #{orderNo}

UPDATE order_group_purc SET
check_time = now(),
check_state = '1' WHERE order_no = #{orderNo}

mapper.select:
SELECT @rownum := @rownum + 1 AS serialNum, b.business_name AS businessName, c.module_name AS moduleName, a.order_group_no AS orderGroupNo, a.order_money AS orderMoney, a.coupon_money AS couponMoney, a.deduction_money AS deductionMoney, a.pay_money AS payMoney, a.pay_type AS payType, a.pay_org AS payOrg, a.trade_complete_time AS tradeCompleteTime, a.pay_check_state AS payCheckState FROM (SELECT @rownum := 0) r, business_balance_detail a LEFT JOIN module_manage c ON a.module_id = c.id, business_info b
a.del_flag = #{DEL_FLAG_NORMAL} AND a.business_info_id = b.id
AND a.business_balance_Id = #{businessBalanceId}
AND a.pay_type = #{payType}
AND a.trade_complete_time between #{balanceStartTime} and date_add(#{balanceEndTime}, interval 1 DAY)
