mapper.select:
SELECT 

FROM log_property_balance a

WHERE a.id = #{id}

SELECT 

FROM log_property_balance a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.balance_start_time >= #{balanceStartTime}


		AND a.balance_end_time <= #{balanceEndTime}


		AND a.check_state = #{checkState}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM log_property_balance a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE log_property_balance SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
