mapper.select:
SELECT 

FROM coupon_manage a

WHERE a.id = #{id}

SELECT 

FROM coupon_manage a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.village_info_id = #{villageInfoId}


		AND a.coupon_name LIKE 
			'%'||#{couponName}||'%'
			'%'+#{couponName}+'%'
			concat('%',#{couponName},'%')


		AND a.time_scope = #{timeScope}


		AND a.active_start_time = #{activeStartTime}


		AND a.active_end_time = date_add(#{activeEndTime}, interval 1 day)


		AND a.active_state = #{activeState}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM coupon_manage a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE coupon_manage SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE coupon_manage SET 
    active_state = #{activeState}
WHERE id = #{id}
