mapper.select:
SELECT 

FROM order_goods a

WHERE a.id = #{id}

		SELECT 

		FROM order_goods a


			a.del_flag = #{DEL_FLAG_NORMAL}

				AND a.order_no = #{orderNo}


				AND a.order_state = #{orderState}


				AND a.pay_state = #{payState}


				AND a.address_type = #{addressType}


				AND a.create_date BETWEEN #{beginCreateDate} AND DATE_ADD(#{endCreateDate},INTERVAL 1 DAY)


                AND a.business_info_id = #{businessInfoId}


                AND a.prod_type = #{prodType}




				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC

SELECT 

FROM order_goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE order_goods SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE order_goods SET 
    order_state = "3",
    over_time = #{overTime},
    update_by = #{updateBy.id},
    update_date = #{updateDate}
WHERE order_no = #{orderNo}

UPDATE order_goods SET 
    order_state = "1",
    update_by = #{updateBy.id},
    update_date = #{updateDate}
WHERE order_no = #{orderNo}

UPDATE order_goods SET 
    order_state = "4",
    update_by = #{updateBy.id},
    update_date = #{updateDate}
WHERE order_no = #{orderNo}

UPDATE order_goods SET 
    order_state = "2",
    update_by = #{updateBy.id},
    update_date = #{updateDate}
WHERE order_no = #{orderNo}
