mapper.select:
SELECT 

FROM building_info a

WHERE a.id = #{id}

SELECT 

FROM building_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM building_info a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


ORDER BY a.update_date DESC

SELECT 

FROM building_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE building_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
