mapper.update:
update tb_goods
set name = #{name,jdbcType=VARCHAR},
  price = #{price,jdbcType=REAL},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  detail = #{detail,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, price, create_time, detail
from tb_goods
where id = #{id,jdbcType=INTEGER}
