mapper.update:
update tb_user
set username = #{username,jdbcType=VARCHAR},
  sex = #{sex,jdbcType=VARCHAR},
  birthday = #{birthday,jdbcType=TIMESTAMP},
  address = #{address,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, username, sex, birthday, address
from tb_user
where id = #{id,jdbcType=INTEGER}
